import { TreeNode, GraphNode, FormatInfo, PathExpressionEngine } from "@atomist/rug/tree/PathExpression";
import { ProjectContext } from "@atomist/rug/operations/ProjectEditor";


export { CommonViewOperations };

/*
 * CommonViewOperations
 */
interface CommonViewOperations {

    /**
      * Evaluate, i.e., compile and execute, JavaScript code.
      * 
      * @param arg0 {any} 
      */
    eval(arg0: any): void;

    /**
      * Cause the operation to fail with a fatal error
      * 
      * @param msg {string} The message to be displayed
      */
    fail(msg: string): void;

    /**
      * Cause the editor to print to the console. Useful for debugging if running editors locally.
      * 
      * @param msg {string} The message to be displayed
      */
    println(msg: string): void;

}   

