import { TreeNode, GraphNode, FormatInfo, PathExpressionEngine } from "@atomist/rug/tree/PathExpression";
import { ProjectContext } from "@atomist/rug/operations/ProjectEditor";
import { ViewSupport } from "./ViewSupport";

export { ContainerTreeNodeView };

/*
 * ContainerTreeNodeView
 */
interface ContainerTreeNodeView extends ViewSupport {

    /**
      * Return the format info for the start of this structure in the file or null if not available
      * 
      * @returns {FormatInfo}
      */
    readonly formatInfo: FormatInfo;

    /**
      * Value
      * 
      * @returns {string}
      */
    value(): string;

    /**
      * Return the value of the given key
      * 
      * @param name {string} The match key whose content you want
      * @returns {any}
      */
    valueOf(name: string): any;

}   

