import {TreeNode,FormatInfo,PathExpressionEngine} from '../tree/PathExpression'
import {ProjectContext} from '../operations/ProjectEditor' 


/*
 * Elm module
 */
interface ElmModule extends TreeNode {

    /**
      * Add a function with the given declaration
      *
      * @param body {string} Body for the function
      */
    addFunction(body: string): void

    /**
      * Update the given module import
      *
      * @param importStatement {string} The complete import statement
      */
    addImportStatement(importStatement: string): void

    /**
      * Children
      *
      * @returns {any[]}
      */
    children(): any[]

    /**
      * Return file content
      *
      * @returns {string}
      */
    content(): string

    /**
      * Does the module expose this?
      *
      * @param name {string} A function or type that might be exposed
      * @returns {boolean}
      */
    exposes(name: string): boolean

    /**
      * Cause the operation to fail with a fatal error
      *
      * @param msg {string} The message to be displayed
      */
    fail(msg: string): void

    /**
      * Return file name, excluding path
      *
      * @returns {string}
      */
    filename(): string

    /**
      * Does the module import the given module?
      *
      * @param moduleName {string} The module name to check
      * @returns {boolean}
      */
    imports(moduleName: string): boolean

    /**
      * Is this file well-formed?
      *
      * @returns {boolean}
      */
    isWellFormed(): boolean

    /**
      * Return the number of lines in the file
      *
      * @returns {number}
      */
    lineCount(): number

    /**
      * Make the file executable
      *
      */
    makeExecutable(): void

    /**
      * Return the name of the module
      *
      * @returns {string}
      */
    name(): string

    /**
      * Tags attached to the node
      *
      * @returns {string[]}
      */
    nodeTags(): string[]

    /**
      * Tags attached to the node
      *
      * @returns {string[]}
      */
    nodeType(): string[]

    /**
      * Return file path, with forward slashes
      *
      * @returns {string}
      */
    path(): string

    /**
      * Return the file's permissions
      *
      * @returns {number}
      */
    permissions(): number

    /**
      * Cause the editor to print to the console. Useful for debugging if running editors locally.
      *
      * @param msg {string} The message to be displayed
      */
    println(msg: string): void

    /**
      * Remove a function with the given name
      *
      * @param name {string} Name of the function to remove
      */
    removeFunction(name: string): void

    /**
      * Change the name of the module
      *
      * @param newName {string} The module name to change to
      */
    rename(newName: string): void

    /**
      * Replace the exposing
      *
      * @param newExposing {string} New content of exposing. Does not include exposing keyword. Will be either a CSV list or ..
      */
    replaceExposing(newExposing: string): void

    /**
      * Does this path begin with the given pattern? Pattern should contain slashes but not begin with a /
      *
      * @param root {string} The root path to begin searching from
      * @returns {boolean}
      */
    underPath(root: string): boolean

    /**
      * Update the given module import
      *
      * @param oldModuleName {string} The old module import name
      * @param newName {string} The module name to change to
      */
    updateImport(oldModuleName: string, newName: string): void

}   

export {ElmModule}
