import { TreeNode, GraphNode, FormatInfo, PathExpressionEngine } from "@atomist/rug/tree/PathExpression";
import { ProjectContext } from "@atomist/rug/operations/ProjectEditor";


export { MutableTerminal };

/*
 * Updatable terminal node
 */
interface MutableTerminal extends TreeNode {

    /**
      * Children
      * 
      * @returns {any[]}
      */
    children(): any[];

    /**
      * Tags attached to the node
      * 
      * @returns {string[]}
      */
    nodeType(): string[];

    /**
      * Update the node value
      * 
      * @param arg0 {string} 
      */
    update(arg0: string): void;

    /**
      * Return the value
      * 
      * @returns {string}
      */
    value(): string;

}   

