import { TreeNode, GraphNode, FormatInfo, PathExpressionEngine } from "@atomist/rug/tree/PathExpression";
import { ProjectContext } from "@atomist/rug/operations/ProjectEditor";


export { OverwritableText };

/*
 * Generic text container
 */
interface OverwritableText extends TreeNode {

    /**
      * Children
      * 
      * @returns {any[]}
      */
    children(): any[];

    /**
      * Return the format info for the start of this structure in the file or null if not available
      * 
      * @returns {FormatInfo}
      */
    readonly formatInfo: FormatInfo;

    /**
      * Tags attached to the node
      * 
      * @returns {string[]}
      */
    nodeType(): string[];

    /**
      * Return the parent node
      * 
      * @returns {TreeNode}
      */
    parent(): TreeNode;

    /**
      * Update the whole value
      * 
      * @param arg0 {string} 
      */
    update(arg0: string): void;

}   

