import { TreeNode, GraphNode, FormatInfo, PathExpressionEngine } from "@atomist/rug/tree/PathExpression";
import { ProjectContext } from "@atomist/rug/operations/ProjectEditor";
import { GitHubId } from "./GitHubId";
import { Issue } from "./Issue";
import { PullRequest } from "./PullRequest";
export { Comment };

/*
 * Type Comment
 */
interface Comment extends GraphNode {

    /**
      * String
      *
      * @returns {string}
      */
    body(): string;

    /**
      * by - Comment -> GitHubId
      *
      * @returns {GitHubId}
      */
    by(): GitHubId;

    /**
      * commentOn - Comment -> PullRequest
      *
      * @returns {PullRequest}
      */
    commentOn(): PullRequest;

    /**
      * String
      *
      * @returns {string}
      */
    id(): string;

    /**
      * on - Comment -> Issue
      *
      * @returns {Issue}
      */
    on(): Issue;

    /**
      * String
      *
      * @returns {string}
      */
    timestamp(): string;

}   

