import { TreeNode, GraphNode, FormatInfo, PathExpressionEngine } from "@atomist/rug/tree/PathExpression";
import { ProjectContext } from "@atomist/rug/operations/ProjectEditor";
import { Comment } from "./Comment";
import { Commit } from "./Commit";
import { GitHubId } from "./GitHubId";
import { Label } from "./Label";
import { Repo } from "./Repo";
export { Issue };

/*
 * Type Issue
 */
interface Issue extends GraphNode {

    /**
      * String
      *
      * @returns {string}
      */
    action(): string;

    /**
      * assigned - Issue -> GitHubId
      *
      * @returns {GitHubId[]}
      */
    assigned(): GitHubId[];

    /**
      * belongsTo - Issue -> Repo
      *
      * @returns {Repo}
      */
    belongsTo(): Repo;

    /**
      * String
      *
      * @returns {string}
      */
    body(): string;

    /**
      * by - Issue -> GitHubId
      *
      * @returns {GitHubId}
      */
    by(): GitHubId;

    /**
      * String
      *
      * @returns {string}
      */
    closedAt(): string;

    /**
      * closedBy - Issue -> GitHubId
      *
      * @returns {GitHubId}
      */
    closedBy(): GitHubId;

    /**
      * String
      *
      * @returns {string}
      */
    createdAt(): string;

    /**
      * hasComment - Issue -> Comment
      *
      * @returns {Comment[]}
      */
    hasComment(): Comment[];

    /**
      * String
      *
      * @returns {string}
      */
    id(): string;

    /**
      * labelled - Issue -> Label
      *
      * @returns {Label[]}
      */
    labelled(): Label[];

    /**
      * long
      *
      * @returns {number}
      */
    number(): number;

    /**
      * resolvedBy - Issue -> Commit
      *
      * @returns {Commit[]}
      */
    resolvedBy(): Commit[];

    /**
      * String
      *
      * @returns {string}
      */
    state(): string;

    /**
      * String
      *
      * @returns {string}
      */
    timestamp(): string;

    /**
      * String
      *
      * @returns {string}
      */
    title(): string;

    /**
      * String
      *
      * @returns {string}
      */
    updatedAt(): string;

}   

