import { TreeNode, GraphNode, FormatInfo, PathExpressionEngine } from "@atomist/rug/tree/PathExpression";
import { ProjectContext } from "@atomist/rug/operations/ProjectEditor";
import { Build } from "./Build";
import { Commit } from "./Commit";
import { Stage } from "./Stage";
export { Pipeline };

/*
 * Type Pipeline
 */
interface Pipeline extends GraphNode {

    /**
      * String
      *
      * @returns {string}
      */
    application(): string;

    /**
      * String
      *
      * @returns {string}
      */
    eventType(): string;

    /**
      * String
      *
      * @returns {string}
      */
    executionId(): string;

    /**
      * has - Pipeline -> Stage
      *
      * @returns {Stage[]}
      */
    has(): Stage[];

    /**
      * runs - Pipeline -> Build
      *
      * @returns {Build[]}
      */
    runs(): Build[];

    /**
      * String
      *
      * @returns {string}
      */
    stageName(): string;

    /**
      * String
      *
      * @returns {string}
      */
    stageType(): string;

    /**
      * String
      *
      * @returns {string}
      */
    taskName(): string;

    /**
      * triggeredBy - Pipeline -> Commit
      *
      * @returns {Commit}
      */
    triggeredBy(): Commit;

    /**
      * boolean
      *
      * @returns {boolean}
      */
    waitingForJudgement(): boolean;

}   

