import { TreeNode, GraphNode, FormatInfo, PathExpressionEngine } from "@atomist/rug/tree/PathExpression";
import { ProjectContext } from "@atomist/rug/operations/ProjectEditor";
import { ArtifactContainer } from "./ArtifactContainer";
import { CommonViewOperations } from "./CommonViewOperations";
import { MutableView } from "./MutableView";
export { Directory };

/*
 * Type for a directory within a project.
 */
interface Directory extends ArtifactContainer, CommonViewOperations, MutableView {

    /**
      * Return the number of files directly in this directory
      *
      * @returns {number}
      */
    fileCount(): number;

    /**
      * Return the name of the directory
      *
      * @returns {string}
      */
    name(): string;

    /**
      * Return the path of the directory
      *
      * @returns {string}
      */
    path(): string;

}   

