import { TreeNode, GraphNode, FormatInfo, PathExpressionEngine } from "@atomist/rug/tree/PathExpression";
import { ProjectContext } from "@atomist/rug/operations/ProjectEditor";
import { ChatChannel } from "./ChatChannel";
import { ChatTeam } from "./ChatTeam";
import { Person } from "./Person";
export { ChatId };

/*
 * Type ChatId
 */
interface ChatId extends GraphNode {

    /**
      * String
      *
      * @returns {string}
      */
    id(): string;

    /**
      * memberOfChannel - ChatId -> ChatChannel
      *
      * @returns {ChatChannel[]}
      */
    memberOfChannel(): ChatChannel[];

    /**
      * memberOfTeam - ChatId -> ChatTeam
      *
      * @returns {ChatTeam}
      */
    memberOfTeam(): ChatTeam;

    /**
      * of - ChatId -> Person
      *
      * @returns {Person}
      */
    of(): Person;

    /**
      * String
      *
      * @returns {string}
      */
    screenName(): string;

}   

