import { TreeNode, GraphNode, FormatInfo, PathExpressionEngine } from "@atomist/rug/tree/PathExpression";
import { ProjectContext } from "@atomist/rug/operations/ProjectEditor";
import { ChatChannel } from "./ChatChannel";
import { ChatId } from "./ChatId";
import { Org } from "./Org";
export { ChatTeam };

/*
 * Type ChatTeam
 */
interface ChatTeam extends GraphNode {

    /**
      * hasChannel - ChatTeam -> ChatChannel
      *
      * @returns {ChatChannel[]}
      */
    hasChannel(): ChatChannel[];

    /**
      * hasMembers - ChatTeam -> ChatId
      *
      * @returns {ChatId[]}
      */
    hasMembers(): ChatId[];

    /**
      * String
      *
      * @returns {string}
      */
    id(): string;

    /**
      * String
      *
      * @returns {string}
      */
    name(): string;

    /**
      * owns - ChatTeam -> Org
      *
      * @returns {Org[]}
      */
    owns(): Org[];

}   

