import { TreeNode, GraphNode, FormatInfo, PathExpressionEngine } from "@atomist/rug/tree/PathExpression";
import { ProjectContext } from "@atomist/rug/operations/ProjectEditor";
import { ChatId } from "./ChatId";
import { Email } from "./Email";
import { GitHubId } from "./GitHubId";
export { Person };

/*
 * Type Person
 */
interface Person extends GraphNode {

    /**
      * String
      *
      * @returns {string}
      */
    forename(): string;

    /**
      * hasChatIdentity - Person -> ChatId
      *
      * @returns {ChatId}
      */
    hasChatIdentity(): ChatId;

    /**
      * hasEmail - Person -> Email
      *
      * @returns {Email[]}
      */
    hasEmail(): Email[];

    /**
      * hasGithubIdentity - Person -> GitHubId
      *
      * @returns {GitHubId}
      */
    hasGithubIdentity(): GitHubId;

    /**
      * String
      *
      * @returns {string}
      */
    surname(): string;

}   

