import { TreeNode, GraphNode, FormatInfo, PathExpressionEngine } from "@atomist/rug/tree/PathExpression";
import { ProjectContext } from "@atomist/rug/operations/ProjectEditor";
import { Build } from "./Build";
import { ChatChannel } from "./ChatChannel";
import { Issue } from "./Issue";
import { Label } from "./Label";
import { Org } from "./Org";
export { Repo };

/*
 * Type Repo
 */
interface Repo extends GraphNode {

    /**
      * channel - Repo -> ChatChannel
      *
      * @returns {ChatChannel[]}
      */
    channel(): ChatChannel[];

    /**
      * hasBuilds - Repo -> Build
      *
      * @returns {Build[]}
      */
    hasBuilds(): Build[];

    /**
      * hasIssues - Repo -> Issue
      *
      * @returns {Issue[]}
      */
    hasIssues(): Issue[];

    /**
      * String
      *
      * @returns {string}
      */
    name(): string;

    /**
      * ownedBy - Repo -> Org
      *
      * @returns {Org}
      */
    ownedBy(): Org;

    /**
      * String
      *
      * @returns {string}
      */
    owner(): string;

    /**
      * uses - Repo -> Label
      *
      * @returns {Label[]}
      */
    uses(): Label[];

}   

