import { TreeNode, GraphNode, FormatInfo, PathExpressionEngine } from "@atomist/rug/tree/PathExpression";
import { ProjectContext } from "@atomist/rug/operations/ProjectEditor";
import { Container } from "./Container";
import { Pod } from "./Pod";
export { Spec };

/*
 * Type Spec
 */
interface Spec extends GraphNode {

    /**
      * creates - Spec -> Pod
      *
      * @returns {Pod[]}
      */
    creates(): Pod[];

    /**
      * String
      *
      * @returns {string}
      */
    curHash(): string;

    /**
      * String
      *
      * @returns {string}
      */
    fsha(): string;

    /**
      * String
      *
      * @returns {string}
      */
    kind(): string;

    /**
      * String
      *
      * @returns {string}
      */
    name(): string;

    /**
      * String
      *
      * @returns {string}
      */
    org(): string;

    /**
      * references - Spec -> Container
      *
      * @returns {Container[]}
      */
    references(): Container[];

    /**
      * String
      *
      * @returns {string}
      */
    repo(): string;

    /**
      * String
      *
      * @returns {string}
      */
    timestamp(): string;

}   

