import { TreeNode, GraphNode, FormatInfo, PathExpressionEngine } from "@atomist/rug/tree/PathExpression";
import { ProjectContext } from "@atomist/rug/operations/ProjectEditor";
import { Commit } from "./Commit";
import { Container } from "./Container";
import { Release } from "./Release";
export { Tag };

/*
 * Type Tag
 */
interface Tag extends GraphNode {

    /**
      * String
      *
      * @returns {string}
      */
    description(): string;

    /**
      * String
      *
      * @returns {string}
      */
    name(): string;

    /**
      * onCommit - Tag -> Commit
      *
      * @returns {Commit}
      */
    onCommit(): Commit;

    /**
      * onContainer - Tag -> Container
      *
      * @returns {Container}
      */
    onContainer(): Container;

    /**
      * String
      *
      * @returns {string}
      */
    ref(): string;

    /**
      * releasedBy - Tag -> Release
      *
      * @returns {Release}
      */
    releasedBy(): Release;

    /**
      * String
      *
      * @returns {string}
      */
    timestamp(): string;

}   

