import { TreeNode, GraphNode, FormatInfo, PathExpressionEngine } from "@atomist/rug/tree/PathExpression";
import { ProjectContext } from "@atomist/rug/operations/ProjectEditor";
import { BodyDeclarationView } from "./BodyDeclarationView";
import { CommonViewOperations } from "./CommonViewOperations";
import { MutableView } from "./MutableView";
export { JavaConstructor };

/*
 * Java constructor
 */
interface JavaConstructor extends BodyDeclarationView, CommonViewOperations, MutableView {

    /**
      * Return the Javadoc for the constructor, or an empty string if there isn't any
      *
      * @returns {string}
      */
    javadoc(): string;

    /**
      * Return the name of the constructor
      *
      * @returns {string}
      */
    name(): string;

    /**
      * Return the number of constructor parameters
      *
      * @returns {number}
      */
    parametersSize(): number;

}   

