import { TreeNode, GraphNode, FormatInfo, PathExpressionEngine } from "@atomist/rug/tree/PathExpression";
import { ProjectContext } from "@atomist/rug/operations/ProjectEditor";
import { ProjectDecorating } from "./ProjectDecorating";
import { ChangeLogging } from "./ChangeLogging";
import { CommonViewOperations } from "./CommonViewOperations";
import { MutableView } from "./MutableView";
export { JavaProject };

/*
 * Java project
 */
interface JavaProject extends ProjectDecorating, ChangeLogging, CommonViewOperations, MutableView {

    /**
      * Is this a Maven project?
      *
      * @returns {boolean}
      */
    isMaven(): boolean;

    /**
      * Is this a Spring project?
      *
      * @returns {boolean}
      */
    isSpring(): boolean;

    /**
      * Is this a Spring Boot project?
      *
      * @returns {boolean}
      */
    isSpringBoot(): boolean;

    /**
      * Return the number of Java files in this module
      *
      * @returns {number}
      */
    javaFileCount(): number;

    /**
      * List the packages in this project
      *
      * @returns {any[]}
      */
    packages(): any[];

    /**
      * Rename the given package. All package under it will also be renamed
      *
      * @param oldPackage {string} Old package name
      * @param newPackage {string} The new package name
      */
    renamePackage(oldPackage: string, newPackage: string): void;

}   

