import { TreeNode, GraphNode, FormatInfo, PathExpressionEngine } from "@atomist/rug/tree/PathExpression";
import { ProjectContext } from "@atomist/rug/operations/ProjectEditor";
import { FileArtifactBacked } from "./FileArtifactBacked";
import { MutableView } from "./MutableView";
import { CommonViewOperations } from "./CommonViewOperations";
import { JavaProject } from "./JavaProject";
export { JavaSource };

/*
 * Java source file
 */
interface JavaSource extends FileArtifactBacked, MutableView, CommonViewOperations {

    /**
      * Return the Java project
      *
      * @returns {JavaProject}
      */
    javaProject(): JavaProject;

    /**
      * Move the source file to the given package
      *
      * @param newPackage {string} The package to move the source file to
      */
    movePackage(newPackage: string): void;

    /**
      * Return the package name
      *
      * @returns {string}
      */
    pkg(): string;

    /**
      * Count the types in this source file
      *
      * @returns {number}
      */
    typeCount(): number;

}   

