"use strict";
/**
 * Result of executing a single test.
 */
var Result = (function () {
    function Result(result, message) {
        this.result = result;
        this.message = message;
    }
    Result.Failure = function (why) {
        return new Result(false, why);
    };
    return Result;
}());
Result.Success = new Result(true, "OK");
exports.Result = Result;
